import logo from './logo.svg';
import React, {Component} from "react";
import './App.css';
import axios from "axios";

class App extends React.Component{
    state = {
        categories: []
    }

  componentDidMount() {
    let data;

    axios.get('http://127.0.0.1:8000/api/categories/')
    .then(res => {
      data = res.data;
      this.setState({
          categories: data
      });
      console.log(this.state.categories)
    })
    .catch(err => {})
  }


    render() {
        return (
            <div className="App">
                <div className="contact">
                    <ul className="responsive-table">
                        <li className="table-header">
                            <div className="col col-1">Id</div>
                            <div className="col col-2">Заголовок</div>
                            </li>
              {this.state.categories.map( c =>
                <li className="table-row" key={c.id}>
                  <div className="col col-1">{c.id}</div>
                  <div className="col col-2">{c.name}</div>
                </li>
                        )}
                    </ul>
                    <div className="contact__button">

                        <a href="/admin">
                            <button className="cybr-btn">
                                Перейти в админ-панель<span aria-hidden></span>
                                <span aria-hidden className="cybr-btnglitch">Cyber</span>
                                <span aria-hidden className="cybr-btntag">SUN</span>
                            </button>
                        </a>

                    </div>
                </div>

            </div>
        );
    }


}

export default App;
